import {React, useState} from 'react';


const RemoveList = (props) =>{

  const [hide, setHide] = useState(false);
  const hideHandler = () =>{
    setHide(true);
  }
  return(<>
    {hide && <a onHide={hideHandler}><i className='fa fa-remove'></i></a>}
  </>
  )


}

export default RemoveList;
