import React from "react";
import RemoveList from "../RemoveList/RemoveList";
// import RemoveList from "../RemoveList/RemoveList";
import Card from "../UI/Card/Card";
import classes from './AddTodoList.module.css';


const AddTodoList = (props) => {

  // const removeTodoList = () =>{
  //   console.log('hey');
  // }
  const [hidden, setHidden] = useState(false);


  // const removeList = (event) => {
  //   event.preventDefault();
  //   // console.log('clicked');
  //   // console.log(event);
  //   setHidden((prevState)=>({
  //     return prevState.list.filter(todo => todo!=value)

  //   }));
  // }
  return (
    <Card classes={classes.users}>
      <ul>
         {props.data.map((item) => (
          <li key={item.id} onHandlerRemove={props.handleFilter}>{item.list}
          </li>
        ))}
          {!hidden && <RemoveList onClick={props.onHandlerRemove}/>}
      </ul>

    </Card>
  )

}

export default AddTodoList;
