// import Users from './components/Users';
import { useState } from "react";
import Header from "./components/Header/Header";
import AddTodo from "./components/AddTodo/AddTodo";
import AddTodoList from "./components/AddTodoList/AddTodoList";

function App() {

  const [todo, setTodo] = useState([]);
  const [deleteTodo, setDeleteTodo] = useState(todo);

  const deleteTodoHandler =(value) =>{
    setDeleteTodo((prevState)=>(prevState.todo.filter(t => t!==value)
    ))
  };
  const todoHandler = (todoList)=>{
    setTodo ((prev) =>{
      return [...prev, {list:todoList, id:Math.random().toString()}]
    })
  }

  return (
    <div>
      <Header />
      <AddTodo onTodoSubmit ={todoHandler} />
      <AddTodoList data={todo} onDeleteTodo={deleteTodoHandler}/>
    </div>
  );
}

export default App;
