  const [todo, setTodo] = useState('');

  const todoHandler = (todoList)=>{
    return ((prev) =>{
      return [...prev, todoList]
    })
  }

  return (
    <div>
      <Header />
      <AddTodo onTodoSubmit ={todoHandler} />
    </div>
  );
}

